import React from "react";
import ErrorMessage from "../Error/Error";
import "./AddUser.css";

const AddUser = () => {
  const [name, setName] = React.useState("");
  const [age, setAge] = React.useState("");

  const [flag, setFlag] = React.useState(false);
  /* const filterHandler = (event) => {
    if (
      (event.target.age < 0 && event.target.age !== undefined) ||
      event.target.name.length === 0
    ) {
      setFlag(true);
    }
  }; */

  const submitUser = (event) => {
    event.preventDefault();
    setName(event.target.name);
    setAge(event.target.age);
  };

  const resetHandler = () => {
    setName("");
    setAge(0);
  };
  console.log(flag);
  return (
    <form onSubmit={!flag && submitUser} className="input">
      <label className="label">Introduce your name</label>
      <input id="name" className="input" type="text" />
      <br />
      <label className="label">Introduce your age</label>
      <input
        id="age"
        className="input"
        type="Number"
        // onChange={filterHandler}
      />
      <button onClick={resetHandler} className="button">
        Reset
      </button>
      <button type="submit" className="button">
        Save user
      </button>

      {flag && <ErrorMessage />}
    </form>
  );
};

export default AddUser;
