import React from "react";
import "./AddUser.css";

const AddUser = () => {
  let emptyUser = { name: "", age: undefined };
  const [user, setUser] = React.useState(emptyUser);

  let flag = false;
  const ageHandler = (event) => {
    if (event.target.age < 0) flag = true;
  };

  const submitUser = (event) => {
    if (!flag) {
      setUser((prevUser) => {
        return {
          ...prevUser,
          name: event.target.name,
          age: event.target.age,
        };
      });
    } else return <ErrorMessage />;
  };

  const resetHandler = () => {
    setUser(emptyUser);
  };

  return (
    <form onSubmit={submitUser} className="input">
      <label className="label">Introduce your name</label>
      <input id="name" className="input" type="text" />
      <br />
      <label className="label">Introduce your age</label>
      <input id="age" className="input" type="Number" onChange={ageHandler} />
      <button onClick={resetHandler} className="button">
        Reset
      </button>
      <button type="submit" className="button">
        Save user
      </button>
    </form>
  );
};

export default AddUser;
